		where ${type} like '%'||#{keyword}||'%' and order_status = #{status}
	
	
	



order by no desc
)A)where rn >= #{start} and rn <=#{end}


select count(*) from order_list	

	
	
	
		where ${type} like '%'||#{keyword}||'%'
	
	
		where order_status = #{status}
	
	
		where ${type} like '%'||#{keyword}||'%' and order_status = #{status}
