mapper.select:

select * from(
	select rownum rn, A.* from (
		
		select * from qna
		
			where category=#{category}
		
		order by no desc
	
	)A
) where rn between #{start} and #{end}


select * from(
	select rownum rn, A.* from (
		
		select * from notice
		   where type is null
		
			and title like '%' || #{keyword} || '%'
		
		
			and status='고객'
		
		order by no desc
		
	)A
) where rn between #{start} and #{end}


select * from(
	select rownum rn, A.* from (
		
		select * from notice
		   where type is null
		
			and title like '%' || #{keyword} || '%'
		
		
			and status='업주'
		
		order by no desc
		
	)A
) where rn between #{start} and #{end}

select * from(
	select rownum rn, A.* from (
		
		select * from OneAndOne order by no desc
	
	)A
) where rn between #{start} and #{end}
