mapper.select:

select * from(
	select rownum rn, A.* from (
		
		select * from qna
		 where 1=1
		
		and  category=#{category}
		
		and  use_yn like '%'||'Y'||'%'
		order by no desc
	
	)A
) where rn between #{start} and #{end}


select * from(
	select rownum rn, A.* from (
		
		select * from notice
		   where type is null
		
			and title like '%' || #{keyword} || '%'
		
		
			and status='고객'
		
		order by no desc
		
	)A
) where rn between #{start} and #{end}


select * from(
	select rownum rn, A.* from (
		
		select * from notice
		   where type is null
		
			and title like '%' || #{keyword} || '%'
		
		
			and status='업주'
		
		order by no desc
		
	)A
) where rn between #{start} and #{end}

select * from(
		select rownum rn, A.* from (
			
			select * from oneandone
			   where 1=1
			
				and reply_status = #{apply_status} 
			
			
				and ${keyword_type} like '%' || #{keyword} || '%'
			
			
			
			and regist_date between to_date(#{start_date}, 'YYYY-MM-DD') and to_date(#{end_date}, 'YYYY-MM-DD') + 1 - 1 / 864000 
			
				and type='고객' and reply_status ='답변대기'
	order by no desc
			
		)A
	) where rn between #{start} and #{end}


select * from(
	select rownum rn, A.* from (
		
		select * from oneandone
		   where 1=1
	
		
			and reply_status = #{apply_status} 
		
		
			and ${keyword_type} like '%' || #{keyword} || '%'
		
		
		
		and regist_date between to_date(#{start_date}, 'YYYY-MM-DD') and to_date(#{end_date}, 'YYYY-MM-DD') + 1 - 1 / 864000 
		
		
			and type='고객'
		
		order by no desc
		
	)A
) where rn between #{start} and #{end}

mapper.update:
update OneAndOne
	set
		reply_content = #{reply_content}
	where
		no = #{no}

mapper.select:
		
		select * from(
			select rownum rn, A.* from (
				
				select * from oneandone
				   where 1=1
 				
					and reply_status = #{apply_status} 
				
				
					and ${keyword_type} like '%' || #{keyword} || '%'
				
				
				
				and regist_date between to_date(#{start_date}, 'YYYY-MM-DD') and to_date(#{end_date}, 'YYYY-MM-DD') + 1 - 1 / 864000 
				
					and type='업주'
				order by no desc
				
			)A
		) where rn between #{start} and #{end}

	select * from(
			select rownum rn, A.* from (
				
