mapper.select:
select * from (select rownum rn, TMP.* from ( select s.no, s.shop_name, s.min_price, s.min_time, s.shop_img, nvl(avg(r.score), 0) score, count(r.score) review from shop s left outer join orders o on s.no = o.shop_code left outer join review r on o.no=r.order_code where s.apply_status='승인완료' and s.contract_status='계약중'
and category = #{cat_no}
group by s.no, s.shop_name, s.min_price, s.min_time, s.shop_img
order by score desc
order by review desc
order by no desc
)TMP ) where rn between #{start} and #{end}

select count(*) from shop 
where apply_status='승인완료' and contract_status='계약중'

	and category = #{cat_no}
